import axios from 'axios';

const api = axios.create({
    baseURL: '/api' // O proxy no package.json cuidará do resto em desenvolvimento
});

export const getTrades = () => api.get('/trades');
export const addTrade = (trade) => api.post('/trades', trade);
export const updateTrade = (id, trade) => api.put(`/trades/${id}`, trade);
export const deleteTrade = (id) => api.delete(`/trades/${id}`);
