import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/Card";
import { cn } from "../lib/utils";

const TradeCard = ({ trade, onClick }) => {
    const isProfit = trade.resultado >= 0;

    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        return new Date(dateString).toLocaleDateString('pt-BR', { timeZone: 'UTC' });
    }

    return (
        <Card 
            className="cursor-pointer hover:shadow-lg transition-shadow duration-200"
            onClick={() => onClick(trade)}
        >
            <CardHeader>
                <CardTitle className="flex justify-between items-center">
                    <span>{trade.ticker}</span>
                    <span className={cn(
                        "text-lg font-bold",
                        isProfit ? "text-green-500" : "text-red-500"
                    )}>
                        {isProfit ? '+' : ''}R$ {trade.resultado}
                    </span>
                </CardTitle>
            </CardHeader>
            <CardContent className="grid gap-2">
                <div className="text-sm text-muted-foreground">
                    <p>Entrada: {formatDate(trade.data_entrada)} @ R$ {trade.preco_entrada}</p>
                    <p>Sa√≠da: {formatDate(trade.data_saida)} @ R$ {trade.preco_saida}</p>
                </div>
                {trade.setup && <p className="text-sm">Setup: {trade.setup}</p>}
            </CardContent>
        </Card>
    );
};

export default TradeCard;
